  checkUsername(username){
    let userRef = firebase.database().ref('users/').orderByChild("displayName").equalTo(username);
    userRef.once('value', users => {
      if (users.exists()) {
        users.forEach(data => {
          if (data.val().displayName === username) {
            console.log('found', data.val().displayName);
            this.util.showToast('Username already exist.', 'danger', 'bottom');
            this.isLogin = false;
          }
        });
      }
      else {
        console.log('not found');
        this.signinWithPhoneNumber();
      }
    });
  }

  goFavourite() {
    firebase.auth().onAuthStateChanged(user => {
      if (user) {

        firebase.database().ref('users/' + user.uid).once('value', (snapshot) => {
          this.sendMessage(user.uid, snapshot.val().photoURL, snapshot.val().displayName, snapshot.val().fcm_token);
        });

        this.navCtrl.navigateRoot(['/complete-profile']);

      }
    });
    // const navData: NavigationExtras = {
    //   queryParams: {
    //     from: 'register',
    //   }
    // };
    // this.router.navigate(['favourite'], navData);
  }

  goFBlogin() {
    firebase.auth().onAuthStateChanged(user => {
      if (user) {
        firebase.database().ref('users').child(user.uid).update({
          displayName: user.displayName,
          photoURL: user.photoURL,
          uid: user.uid,
          rate: false,
          email: user.email,
          points: 100,
          gender: '',
          about: '',
          location: '',
          type: 'users',
          block: false,
          IamFollowings: false,
          following: [],
          followingCount: 0,
          followers: [],
          followersCount: 0,
          nationality: '',
          dob: '',
          fcm_token: localStorage.getItem('fcm') ? localStorage.getItem('fcm') : '',
          phoneNumber: '',
          interestfor: '',
          marital: '',
          education: '',
        });

        firebase.database().ref('users/' + user.uid).once('value', (snapshot) => {
          this.sendMessage(user.uid, snapshot.val().photoURL, snapshot.val().displayName, snapshot.val().fcm_token);
        });

        this.navCtrl.navigateRoot(['/complete-profile']);

      }
    });
  }

  async sendMessage(userId, photo, displayName, fcm_token) {
    let txt = "Hello "+ displayName + ", thank you for Signing up with Mopays App. Mopays is an app with multiple features to help Mauritians around the world as well as tourists.<br><br>Our key Features are :<br>Explore<br>Business<br>News<br>Annonces<br>Jobs<br>Services<br>Events<br>Weather<br>Emergency Numbers<br>Chat<br>Radio<br>Photo Gallery<br>QR Code Scanner<br>Top Apps<br>And much more ..<br><br>Browse the app through the different available Menus ( top , down and middle one where you can go through the main sections easily)<br><br>Feel free to reply this message if you are facing any problems, we will be very happy to help you. 😊<br><br>You can also join our highly engaging Facebook page, Mopays and message us.<br><br>Share the love and invite friends to download our App<br><br>See you soon ..<br>"
    let message = { read: false, type: 'text', fromID: this.id, toID: userId, msg: txt, fromPhoto: this.photoURL, toPhoto: photo, fromName: this.displayName, toName: displayName, timeStamp: Date.now() };
    let key = await this.db.list(`messages`).push(message).key;

    //await this.db.object(`messages/${key}`).set({chatKey: key, type: 'text', fromID: this.myuid, toID: this.userId, msg: this.msg, fromPhoto: this.myPhoto, toPhoto: this.photoURL, fromName: this.myName, toName: this.displayName, timeStamp: Date.now()});

    await this.db.list(`message-by-user/${userId}/${this.id}`).push({ msgkey: key, unread: 1 });
    await this.db.list(`message-by-user/${this.id}/${userId}`).push({ msgkey: key, unread: 0 });

    await this.db.object(`last-messages/${userId}/${this.id}`).set({ lastmsg: key });
    await this.db.object(`last-messages/${this.id}/${userId}`).set({ lastmsg: key });
    this.util.sendNotification( `${txt}`, `${this.displayName}`, fcm_token).subscribe((data) => {
      console.log('send notifications', data);
      console.log('tokens', fcm_token);
     }, error => {
    console.log(error);
   });
  }

